      Out->isDefined[i] = In->isDefined[i];
      Out->lineNumber[i] = In->lineNumber[i];
      Out->nameSize[i] = In->nameSize[i];
      Out->name[i] = (char *)bmalloc((Out->nameSize[i] + 1) * sizeof(char));
