int ppParse_bisonerror(char *s)
/***************************************************************\
* USAGE:                                                        *
* ARGUMENTS:                                                    *
* RETURN VALUES:                                                *
* NOTES: called when preprocessor reaches an error              *
\***************************************************************/
{ // error in parser
  if (!strcmp(s,"invalid character"))
    printf("ERROR: The '%s' is an %s in system statement %d.\n", ppParse_flextext, s, ppParse_flexlineno);
  else
    printf("ERROR: There was an error (%s) near '%s' in system statement %d.\n", s, ppParse_flextext, ppParse_flexlineno);
  errorStatement();

  return 0;
}

